/// <reference types="@rbxts/types" />
import { StaticArguments } from "./internal";
/**
 * An event that behaves like a function
 * @rbxts server
 */
export default class NetServerAsyncFunction<C extends Array<any> = Array<unknown>> {
    private instance;
    private timeout;
    private listeners;
    private connector;
    protected propTypes: C | undefined;
    constructor(name: string, ...recievedPropTypes: C);
    GetCallTimeout(): number;
    SetCallTimeout(timeout: number): void;
    SetCallback(callback: (player: Player, ...args: StaticArguments<C>) => any): void;
    CallPlayerAsync(player: Player, ...args: Array<unknown>): Promise<unknown>;
}
